mapper.select:
select
from SYS_USER a
join SYS_USER_ROLE ur on ur.C_USER_ID= a.C_USER_ID join SYS_ROLE r on ur.C_ROLE_ID = r.C_ROLE_ID where a.DEL_FLAG = 0 and r.C_ROLE_ID = #{roleId}

mapper.update:
UPDATE SYS_USER SET
 LAST_LOGIN_IP = #{lastLoginIp},
LAST_LOGIN_TIME = #{lastLoginTime}
WHERE C_USER_ID = #{userId}
